import { useContext, useState } from 'react'
import { StyleSheet, View } from 'react-native'
// import { useAuth } from '../../useAuth'
import Button from './Button'
import р from './Button'
// import Error from '../UI/Error'
import AsyncStorage from '@react-native-async-storage/async-storage'
import Field from './Field'

const AuthForm = () => {
	const [login, setLogin] = useState('')
	const [password, setPassword] = useState('')
	const [error, setError] = useState('')

	// const { isAuth, setIsAuth } = useAuth()

	const authHandler = async () => {
		if (login && password) {
			if (login !== '1103612') {
				return setError('Не верный Email')
			}

			if (password !== 'QWE123qwe') {
				return setError('Не верный пароль')
			}

			const response = await fetch('http://localhost:3006/api/v1/user/signin', {
				method: "POST",
				headers: {
					"Content-Type": "application/json"
				},
				credentials: 'include',
				body: JSON.stringify({ login, password })
			})
			const currentUser = await response.json()

			console.log(currentUser);

			await AsyncStorage.setItem('token', 'w23eefq234Ad')
			setIsAuth(true)

		} else {
			setError('Заполните все поля!')
		}
	}

	return (
		<View style={styles.auth}>
			{/* {error && <Error message={error} />} */}

			<Field value={login} onChangeText={setLogin} placeholder='Login..' />
			<Field
				value={password}
				onChangeText={setPassword}
				placeholder='Password..'
			/>
			<Button onPress={authHandler} title='Войти' />
		</View>
	)
}

const styles = StyleSheet.create({
	auth: {
		paddingTop: '13%',
		backgroundColor: 'rgb(22, 50, 89)',
		height: '100%',
	},
})

export default AuthForm
