import { Text, View } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useEffect } from 'react';
import axios from 'axios';
import Star from '../../components/Star';
import Superstar from '../../components/Superstar';
import { count } from '../../helpers/count';
import React, { useState } from 'react';
import styles from './Deposit';
import { blue, gray } from '../../constants/Colors'

export default function Deposit() {
	const [state, setState] = useState<boolean>(false)
	const [state1, setState1] = useState<boolean>(false)
	const [state2, setState2] = useState<boolean>(false)
	const [state3, setState3] = useState<boolean>(false)

	const getCounts = async () => {
		const token: string = await AsyncStorage.getItem('token')
		const response = await axios({
			method: 'post',
			url: 'http://localhost:3006/api/v1/todos',
			data: {
				token
			}
		});
		console.log(response.data);
	}

	useEffect(() => {
		getCounts()
	}, [])

	const amounts = [
		{
			money: 60006.66,
			number: '1000019705',
			currensy: 'RUB'
		},
		{
			money: 57057.22,
			number: '1000019707',
			currensy: 'EUR'
		},
		{
			money: 4398.60,
			number: '1000019732',
			currensy: 'RUB'
		},
		{
			money: 1919.26,
			number: '1000019706',
			currensy: 'USD'
		},
	]

	const change = () => {
		setState(!state)
	}
	const change1 = () => {
		setState1(!state1)
	}
	const change2 = () => {
		setState2(!state2)
	}
	const change3 = () => {
		setState3(!state3)
	}

	return (
		<View style={styles.container}>
			<Text style={styles.heading}>Торговые счета</Text>
			<View style={styles.block}>
				<Superstar onClick={change} fill={state ?'rgb(22, 50, 89)' : '#DCDCDC'} width={20} height={20}/>
				<View style={styles.miniblock}>
					<Text style={styles.uptext}>{count(amounts[0].money, amounts[0].currensy)[0].concat(',')}<Text style={styles.next}>{count(amounts[0].money, amounts[0].currensy)[1]}</Text></Text>
					<Text style={styles.subtext}>{amounts[0].number} - С хеджированием</Text>
				</View>
			</View>
			<View style={styles.block}>
				<Superstar onClick={change1} fill={state1 ? blue : 'grey'}  width={20} height={20}/>
				<View style={styles.miniblock}>
					<Text style={styles.uptext}>{count(amounts[1].money, amounts[1].currensy)[0].concat(',')}<Text style={styles.next}>{count(amounts[1].money, amounts[1].currensy)[1]}</Text></Text>
					<Text style={styles.subtext}>{amounts[1].number} - С хеджированием</Text>
				</View>
			</View>
			<View style={styles.block}>
				<Superstar onClick={change2} fill={state2 ?'rgb(22, 50, 89)' : 'grey'} width={20} height={20}/>
				<View style={styles.miniblock}>
					<Text style={styles.uptext}>{count(amounts[2].money, amounts[2].currensy)[0].concat(',')}<Text style={styles.next}>{count(amounts[2].money, amounts[2].currensy)[1]}</Text></Text>
					<Text style={styles.subtext}>{amounts[2].number} - С хеджированием</Text>
				</View>
			</View>
			<View style={styles.block}>
				<Superstar onClick={change3} fill={state3 ?'rgb(22, 50, 89)' : 'grey'} width={20} height={20}/>
				<View style={styles.miniblock}>
					<Text style={styles.uptext}>{count(amounts[3].money, amounts[3].currensy)[0].concat(',')}<Text style={styles.next}>{count(amounts[3].money, amounts[3].currensy)[1]}</Text></Text>
					<Text style={styles.subtext}>{amounts[3].number} - С хеджированием</Text>
				</View>
			</View>
		</View>
	);
}
