export const count = (price, simbol) => {
	let changeSymbol = price.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ' ');
	if (simbol === 'RUB') {
		return changeSymbol.concat(' ₽');
	}
	if (simbol === 'EUR') {
		return changeSymbol.concat(' €');
	}
	if (simbol === 'USD') {
		return price.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ' ').concat(' $');
	}
}
